        suffix_set = set()
        valid = True

        for l in range(1, n):
            candidates = length_map[l]
            if prefix_candidate[l-1] == 0:
                prefix_set.add(candidates[0])
                suffix_set.add(candidates[1])
            else:
                prefix_set.add(candidates[1])
                suffix_set.add(candidates[0])

        # Check if there exists a string s of length n that matches all prefixes and suffixes
        for candidate in set(prefix_set) | set(suffix_set):
            s = candidate + 'a' * (n - len(candidate))
            for p in prefix_set:
                if s[:len(p)] != p:
                    valid = False
                    break
            if not valid:
                break
            for suf in suffix_set:
