def main():
    import sys
    data = sys.stdin.read().split()
    n = int(data[0])
    l = float(data[1])
    a = list(map(float, data[2:2+n]))

    if n == 0:
        print("{0:.10f}".format(l / 2))
        return

    a.sort()

    gaps = []

    if a[0] > 0:
        gaps.append(a[0] / 2)

    for i in range(n-1):
        gap = a[i+1] - a[i]
        if gap > 0:
            gaps.append(gap / 2)

    if a[-1] < l:
        gaps.append((l - a[-1]) / 2)

    if not gaps:
        ans = 0.0
    else:
        ans = max(gaps)

    print("{0:.10f}".format(ans))

if __name__ == "__main__":
    main()

import math

def main():
    n = int(input().strip())
    A_str = input().strip()
    B_str = input().strip()

    m = len(A_str)
    k = len(B_str)

    L = (m * k) // math.gcd(m, k)

    full_cycles = n // L
    remainder = n % L

    def outcome(x, y):
        if x == y:
            return (0, 0)
        elif (x, y) in [('R', 'P'), ('S', 'R'), ('P', 'S')]:
            return (0, 1)
        else:
            return (1, 0)

    losses_Nike = 0
    losses_Poly = 0

    for i in range(L):
        idx_A = (i % m)
        idx_B = (i % k)
        move_Nike = A_str[idx_A]
        move_Poly = B_str[idx_B]
        add_Nike, add_Poly = outcome(move_Nike, move_Poly)
        losses_Nike += add_Nike
        losses_Poly += add_Poly

    total_Nike = full_cycles * losses_Nike + min(remainder, L) * [outcome(A_str[i % m], B_str[i % k]) for i in range(L)][remainder][0]
    total_Poly = full_cycles * losses_Poly + min(remainder, L) * [outcome(A_str[i % m], B_str[i % k]) for i in range(L)][remainder][1]

    print(f"{total_Nike} {total_Poly}")

if __name__ == "__main__":
    main()

