        if r_str[i] == '?':
            right_wild.append(i+1)
        else:
            right_fixed.append((r_str[i], i+1))

    fixed_left_dict = defaultdict(list)
    for color, idx in left_fixed:
        fixed_left_dict[color].append(idx)

    fixed_right_dict = defaultdict(list)
    for color, idx in right_fixed:
        fixed_right_dict[color].append(idx)

    total_fixed_matches = 0
    matched_left = set()
    matched_right = set()

    for color in fixed_left_dict:
        if color in fixed_right_dict:
            left_list = fixed_left_dict[color]
            right_list = fixed_right_dict[color]
            min_val = min(len(left_list), len(right_list))
            total_fixed_matches += min_val
            left_list[:min_val] = [x for _, x in sorted(zip(left_list, range(min_val)))]
            right_list[:min_val] = [x for _, x in sorted(zip(right_list, range(min_val)))]
            matched_left.update(left_list[:min_val])
            matched_right.update(right_list[:min_val])

    left_remaining_fixed = []
    for color, indices in fixed_left_dict.items():
        for idx in indices:
            if idx not in matched_left:
                left_remaining_fixed.append((color, idx))

    right_remaining_fixed = []
    for color, indices in fixed_right_dict.items():
        for idx in indices:
            if idx not in matched_right:
                right_remaining_fixed.append((color, idx))

    A = len(left_remaining_fixed)
    B = len(right_remaining_fixed)
    W_u = len(left_wild)
    W_v = len(right_wild)

    m1 = min(A, W_v)
    m2 = min(B, W_u)
    m3 = min(W_u - m2, W_v - m1)

    total_pairs = total_fixed_matches + m1 + m2 + m3

    print(total_pairs)

    output_lines = []
    output_lines.append(str(total_pairs))

    for color, idx in left_remaining_fixed:
