    max_day = 0
    for _ in range(n):
        l = int(next(it))
        r = int(next(it))
        cost_val = int(next(it))
        vouchers.append((l, r, cost_val))
        max_day = max(max_day, r)

    max_duration = max_day
    duration_map = {}
    for (l, r, cost_val) in vouchers:
        dur = r - l + 1
        if dur not in duration_map:
            duration_map[dur] = {'min_cost': cost_val, 'intervals': [(l, r)]}
        else:
            if cost_val < duration_map[dur]['min_cost']:
                duration_map[dur]['min_cost'] = cost_val
            duration_map[dur]['intervals'].append((l, r))

    for dur in list(duration_map.keys()):
        intervals = duration_map[dur]['intervals']
        if len(intervals) > 1:
            min_cost = duration_map[dur]['min_cost']
            new_intervals = []
            for (l, r) in intervals:
                if (l, r) == (intervals[0][0], intervals[0][1]):
                    continue
                else:
                    new_intervals.append((l, r))
            duration_map[dur]['intervals'] = [intervals[0]]
            duration_map[dur]['min_cost'] = min(min_cost, min([cost for (lc, rc, cost) in vouchers if (lc, rc) != (intervals[0][0], intervals[0][1]) and lc == l and rc == r]))
        else:
            pass

    intervals_by_start = {}
    intervals_by_end = {}
    for dur, info in duration_map.items():
        intervals = info['intervals']
        for (l, r) in intervals:
            if l not in intervals_by_start:
                intervals_by_start[l] = []
            intervals_by_start[l].append(r)
            if r not in intervals_by_end:
                intervals_by_end[r] = []
            intervals_by_end[r].append(l)

    for key in intervals_by_start:
        intervals_by_start[key] = sorted(intervals_by_start[key])
    for key in intervals_by_end:
        intervals_by_end[key] = sorted(intervals_by_end[key])

    ans = float('inf')
    found = False
    for dur, info in duration_map.items():
        target_dur = x - dur
        if target_dur not in duration_map:
            continue

        min1 = info['min_cost']
        min2 = duration_map[target_dur]['min_cost']
        if min1 + min2 < ans:
            ans = min1 + min2

        if not found:
            if dur == target_dur:
                if len(info['intervals']) >= 2:
                    intervals_list = info['intervals']
                    A = [inter[0] for inter in intervals_list]
                    B = [inter[1] for inter in intervals_list]
                    i = 0
                    j = len(B) - 1
                    while i < len(A) and j >= 0:
                        if B[i] < A[j+1] or (j == 0 and B[i] < A[0]):
                            ans = min(ans, 2 * min1)
                            found = True
                            break
                        i += 1
                        j -= 1
            else:
                if target_dur in intervals_by_end:
                    ends = intervals_by_end[target_dur]
                    starts = info['intervals'][0][0]
                    if starts in intervals_by_start:
                        starts_list = intervals_by_start[starts]
                    else:
                        starts_list = [info['intervals'][0][0]]
                    if ends:
                        candidate_end = ends[-1]
                        if candidate_end < starts_list[0]:
                            ans = min(ans, min1 + min2)
                            found = True
                    else:
                        continue
                else:
                    continue
        else:
            pass

    if ans == float('inf'):
        print(-1)
    else:
        print(ans)

