    x = int(data[1])
    vouchers = []
    index = 2
    max_day = 200000
    for i in range(n):
        l = int(data[index])
        r = int(data[index+1])
        cost_val = int(data[index+2])
        index += 3
        vouchers.append((l, r, cost_val))

    max_d = 200000
    by_duration = [None] * (max_d + 1)

    for dur in range(1, max_d + 1):
        by_duration[dur] = []

    for (l, r, cost_val) in vouchers:
        dur = r - l + 1
        if dur <= max_d:
            by_duration[dur].append((l, r, cost_val))

    def build_lists(vouchers_list):
        starts = []
        ends = []
        for (l, r, cost_val) in vouchers_list:
            starts.append((l, cost_val))
            ends.append((r, cost_val))
        starts.sort(key=lambda x: x[0])
        ends.sort(key=lambda x: x[0])
        min_costs_start = []
        min_costs_end = []
        cur_min = float('inf')
        for s in starts:
            if s[1] < cur_min:
                cur_min = s[1]
            min_costs_start.append((s[0], cur_min))
        cur_min = float('inf')
        for e in ends:
            if e[1] < cur_min:
                cur_min = e[1]
            min_costs_end.append((e[0], cur_min))
        return starts, min_costs_start, ends, min_costs_end

    def get_min_cost_by_duration(dur, side):
        if dur > max_d or dur < 1 or by_duration[dur] is None or len(by_duration[dur]) == 0:
            return None, float('inf')
        if side == 'start':
            return build_lists(by_duration[dur])
        else:
            return build_lists(by_duration[dur])

    def query_complementary(dur, bound, side):
        if dur < 1 or dur > max_d:
            return float('inf')
        comp_dur = x - dur
        if comp_dur < 1 or comp_dur > max_d:
            return float('inf')
        if by_duration[comp_dur] is None or len(by_duration[comp_dur]) == 0:
            return float('inf')
        starts, min_start, ends, min_end = build_lists(by_duration[comp_dur])
        ans = float('inf')
        if side == 'start':
            pos = bisect.bisect_right(min_start, (bound, float('inf')))
            if pos > 0:
                candidate_l, candidate_cost = min_start[pos-1]
                if candidate_cost < ans:
                    ans = candidate_cost
            return ans
        elif side == 'end':
            pos = bisect.bisect_left(min_end, (bound, float('inf')))
            if pos < len(min_end):
                candidate_r, candidate_cost = min_end[pos]
                if candidate_cost < ans:
                    ans = candidate_cost
            return ans

    best = float('inf')

    for (l, r, cost_val) in vouchers:
        dur = r - l + 1
        if dur >= x:
            continue
        comp_dur = x - dur
        if comp_dur < 1:
            continue

        cand1 = query_complementary(dur, r, 'start')
        cand2 = query_complementary(dur, l, 'end')
        total = cost_val + cand1 if cand1 != float('inf') else float('inf')
        total2 = cost_val + cand2 if cand2 != float('inf') else float('inf')
        if total < best:
            best = total
        if total2 < best:
            best = total2

    if best == float('inf'):
        print(-1)
    else:
        print(best)

