        if not group:
            return None, None
        sorted_group = sorted(group, key=lambda x: x[0])
        n_g = len(sorted_group)
        keys = [item[0] for item in sorted_group]
        min_suffix = [0] * n_g
        min_suffix[-1] = sorted_group[-1][2]
        for i in range(n_g-2, -1, -1):
            min_suffix[i] = min(sorted_group[i][2], min_suffix[i+1])
        return keys, min_suffix

    def query_ds_by_l(keys, min_suffix, t):
        if keys is None:
            return None
        lo, hi = 0, len(keys)-1
        pos = -1
        while lo <= hi:
            mid = (lo + hi) // 2
            if keys[mid] > t:
                pos = mid
                hi = mid - 1
            else:
                lo = mid + 1
        if pos == -1:
            return None
        return min_suffix[pos]

    def build_ds_by_r(group):
        if not group:
            return None, None
        sorted_group = sorted(group, key=lambda x: x[1])
        n_g = len(sorted_group)
        keys = [item[1] for item in sorted_group]
        min_prefix = [0] * n_g
        min_prefix[0] = sorted_group[0][2]
        for i in range(1, n_g):
            min_prefix[i] = min(sorted_group[i][2], min_prefix[i-1])
        return keys, min_prefix

    def query_ds_by_r(keys, min_prefix, t):
        if keys is None:
            return None
        lo, hi = 0, len(keys)-1
        pos = -1
        while lo <= hi:
            mid = (lo + hi) // 2
            if keys[mid] < t:
                pos = mid
                lo = mid + 1
            else:
                hi = mid - 1
        if pos == -1:
            return None
        return min_prefix[pos]

