            candidate = float('inf')
            for i in range(nA):
                l_i, r_i, cost_i = A[i]
                pos1 = bisect_left(A_sorted_by_l, (r_i+1, 0, 0))
                if pos1 < nA:
                    cand1 = min_suffix[pos1] + cost_i
                    if cand1 < candidate:
                        candidate = cand1
                pos2 = bisect_left(A_sorted_by_l, (0, l_i, 0))
                if pos2 > 0:
                    cand2 = min_prefix[pos2-1] + cost_i
                    if cand2 < candidate:
                        candidate = cand2
            if candidate < ans:
                ans = candidate
        else:
            if dur2 not in duration_map:
                continue

            A = duration_map[dur]
            B = duration_map[dur2]

            def helper(group1, group2):
                if not group1 or not group2:
                    return float('inf')
                group1_sorted_by_r = sorted(group1, key=lambda x: x[1])
                group2_sorted_by_l = sorted(group2, key=lambda x: x[0])
                n1 = len(group1_sorted_by_r)
                min_costs = [0] * n1
                min_costs[0] = group1_sorted_by_r[0][2]
                for i in range(1, n1):
                    min_costs[i] = min(min_costs[i-1], group1_sorted_by_r[i][2])

                res = float('inf')
                ptr = 0
                for l_val, r_val, cost_val in group2_sorted_by_l:
                    idx = bisect_left(group1_sorted_by_r, (0, l_val, 0), key=lambda x: x[1])
                    if idx < n1:
                        total = min_costs[idx] + cost_val
                        if total < res:
                            res = total
                return res

            cand1 = helper(A, B)
            cand2 = helper(B, A)
            candidate = min(cand1, cand2)
            if candidate < ans:
                ans = candidate

    if ans == float('inf'):
        print(-1)
