def is_perfect_power(x, m):
    lower = 2
    upper = x
    while lower <= upper:
        mid = (lower + upper) // 2
        power = mid ** m
        if power == x:
            return True
        elif power < x:
            lower = mid + 1
        else:
            upper = mid - 1
    return False

