    idx = 2
    for _ in range(n):
        w = int(data[idx])
        c = int(data[idx + 1])
        items.append((w, c))
        idx += 2

    # Group items by weight
    from collections import defaultdict
    weight_groups = defaultdict(list)
    for w, c in items:
        weight_groups[w].append(c)

    # Sort each group and compute prefix sums
    prefix_sums = {}
    for w in weight_groups:
        weight_groups[w].sort(reverse=True)
        prefix_sums[w] = [0]
        for c in weight_groups[w]:
            prefix_sums[w].append(prefix_sums[w][-1] + c)

    # Try all possible weights (1, 2, 3)
    max_cost = 0
    for w in [1, 2, 3]:
        if w not in prefix_sums:
            continue
        # How many of this weight can we take?
        count = m // w
        if count == 0:
            continue
        # Take the top 'count' items from this weight
        total = prefix_sums[w][count]
        max_cost = max(max_cost, total)

    # Try combinations of different weights
    for w1 in [1, 2, 3]:
        if w1 not in prefix_sums:
            continue
        for w2 in [1, 2, 3]:
            if w2 not in prefix_sums or w2 == w1:
                continue
            # Calculate how many of each weight we can take
            count1 = m // w1
            count2 = m // w2
            if count1 == 0 or count2 == 0:
                continue
            # Try all combinations of count1 and count2 that sum up to <= m
            for cnt1 in range(1, count1 + 1):
                rem = m - cnt1 * w1
                if rem < 0:
                    break
                cnt2 = rem // w2
                if cnt2 > count2:
                    cnt2 = count2
                total = prefix_sums[w1][cnt1] + prefix_sums[w2][cnt2]
                max_cost = max(max_cost, total)

    # Try triplets of different weights
    for w1 in [1, 2, 3]:
        if w1 not in prefix_sums:
            continue
        for w2 in [1, 2, 3]:
            if w2 not in prefix_sums or w2 == w1:
                continue
            for w3 in [1, 2, 3]:
                if w3 not in prefix_sums or w3 == w1 or w3 == w2:
                    continue
                # Calculate how many of each weight we can take
                count1 = m // w1
                count2 = m // w2
                count3 = m // w3
                if count1 == 0 or count2 == 0 or count3 == 0:
                    continue
                # Try all combinations of count1, count2, count3 that sum up to <= m
                for cnt1 in range(1, count1 + 1):
                    rem1 = m - cnt1 * w1
                    if rem1 < 0:
                        break
                    for cnt2 in range(1, count2 + 1):
                        rem2 = rem1 - cnt2 * w2
                        if rem2 < 0:
                            break
                        cnt3 = rem2 // w3
                        if cnt3 > count3:
                            cnt3 = count3
                        total = prefix_sums[w1][cnt1] + prefix_sums[w2][cnt2] + prefix_sums[w3][cnt3]
