import sys
from collections import deque

class BefungeExecutor:
    def __init__(self, code):
        self.code = code
        self.stack = []
        self.ip = [0, 0]  # Instruction pointer (row, column)
        self.direction = [0, 1]  # Initial direction: right
        self.width = len(code[0]) if code else 0
        self.height = len(code) if code else 0
        self.terminated = False
        self.jumps = []

    def get_current_char(self):
        if self.height == 0 or self.width == 0:
            return None
        r, c = self.ip
        if 0 <= r < self.height and 0 <= c < self.width:
            return self.code[r][c]
        return None

    def execute_step(self):
        if self.terminated:
            return True

        char = self.get_current_char()
        if char is None:
            self.terminated = True
            return True

        if char.isdigit():
            self.stack.append(int(char))
            self.advance_ip()
            return False

        elif char == '+':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(a + b)
            self.advance_ip()
            return False

        elif char == '-':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(b - a)
            self.advance_ip()
            return False

        elif char == '*':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(a * b)
            self.advance_ip()
            return False

        elif char == '/':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(b // a)
            self.advance_ip()
            return False

        elif char == '<':
            self.direction = [-1, 0]
            self.advance_ip()
            return False

        elif char == '>':
            self.direction = [1, 0]
            self.advance_ip()
            return False

        elif char == '^':
            self.direction = [0, 1]
            self.advance_ip()
            return False

        elif char == 'v':
            self.direction = [0, -1]
            self.advance_ip()
            return False

        elif char == '_':
            self.direction = [0, 0]
            if self.stack and self.stack[-1] == 0:
                self.direction = [1, 0]
            else:
                self.direction = [-1, 0]
            self.advance_ip()
            return False

        elif char == '|':
            self.direction = [0, 0]
            if self.stack and self.stack[-1] == 0:
                self.direction = [0, -1]
            else:
                self.direction = [0, 1]
            self.advance_ip()
            return False

        elif char == '=':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(1 if a == b else 0)
            self.advance_ip()
            return False

        elif char == '!':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(1 if a != b else 0)
            self.advance_ip()
            return False

        elif char == '?':
            if len(self.stack) < 1:
                self.terminated = True
                return True
            a = self.stack.pop()
            self.stack.append(1 if a != 0 else 0)
            self.advance_ip()
            return False

        elif char == '`':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(1 if a < b else 0)
            self.advance_ip()
            return False

        elif char == '~':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(1 if a > b else 0)
            self.advance_ip()
            return False

        elif char == '@':
            if len(self.stack) < 3:
                self.terminated = True
                return True
            n = self.stack.pop()
            m = self.stack.pop()
            k = self.stack.pop()
            self.stack.append(n)
            self.stack.append(m)
            self.stack.append(k)
            self.advance_ip()
            return False

        elif char == 'p':
            if len(self.stack) < 3:
                self.terminated = True
                return True
            d = self.stack.pop()
            y = self.stack.pop()
            x = self.stack.pop()
            if 0 <= x < self.width and 0 <= y < self.height:
                self.code[y] = self.code[y][:x] + str(d) + self.code[y][x+1:]
                self.width = max(self.width, x+1)
                self.height = max(self.height, y+1)
            self.advance_ip()
            return False

        elif char == ':':
            if len(self.stack) < 1:
                self.terminated = True
                return True
            if self.stack:
                a = self.stack[-1]
                self.stack.append(a)
            self.advance_ip()
            return False

        elif char == '\\':
            if len(self.stack) < 2:
                self.terminated = True
                return True
            a = self.stack.pop()
            b = self.stack.pop()
            self.stack.append(a)
            self.stack.append(b)
            self.advance_ip()
            return False

        elif char == '$':
            if len(self.stack) < 1:
                self.terminated = True
                return True
            if self.stack:
                self.stack.pop()
            self.advance_ip()
            return False

        elif char == '#':
            self.jump(3)
            return False

        elif char == ' ':
            self.advance_ip()
            return False

        else:
            self.terminated = True
            return True

    def advance_ip(self):
        dr, dc = self.direction
        r, c = self.ip
        self.ip = [r + dr, c + dc]

    def jump(self, steps):
        dr, dc = self.direction
        r, c = self.ip
        self.ip = [r + dr * steps, c + dc * steps]

    def set_terminated(self, value):
        self.terminated = value

    def reset(self):
        self.stack = []
        self.ip = [0, 0]
        self.direction = [0, 1]
        self.terminated = False
        self.width = len(self.code[0]) if self.code else 0
        self.height = len(self.code) if self.code else 0

def solve_befunge(code_str):
    lines = code_str.strip().split('\n')
    executor = BefungeExecutor(lines)

    def dfs():
        if executor.terminated:
            return None

        current_char = executor.get_current_char()
        if current_char is None:
            return None

        if current_char.isdigit():
            num = int(current_char)
            rest = dfs()
            if rest is not None:
                return [num] + rest
            return None

        elif current_char in ['+', '-', '*', '/', '=', '!', '`', '~', ':', '\\', '$']:
            success = executor.execute_step()
            if success:
                return dfs()
            else:
                return None

        elif current_char in ['<', '>', '^', 'v', '_', '|']:
            success = executor.execute_step()
            if success:
                return dfs()
            else:
                return None

        elif current_char == '?':
            executor.stack.append(1)
            success = executor.execute_step()
            if success:
                return dfs()
            else:
                return None

        elif current_char == '#':
            steps = executor.jump(3)
            return dfs()

        elif current_char == 'p':
            if len(executor.stack) < 3:
                return None
            d = executor.stack.pop()
            y = executor.stack.pop()
            x = executor.stack.pop()
            if 0 <= x < executor.width and 0 <= y < executor.height:
                executor.code[y] = executor.code[y][:x] + str(d) + executor.code[y][x+1:]
                executor.width = max(executor.width, x+1)
                executor.height = max(executor.height, y+1)
            success = executor.execute_step()
            if success:
                return dfs()
            else:
                return None

        elif current_char == '@':
            if len(executor.stack) < 3:
                return None
            n = executor.stack.pop()
            m = executor.stack.pop()
            k = executor.stack.pop()
            executor.stack.append(n)
            executor.stack.append(m)
            executor.stack.append(k)
            success = executor.execute_step()
            if success:
                return dfs()
            else:
                return None

        elif current_char == '.' or current_char == ',': 
            if not executor.stack:
                return None
            num = executor.stack.pop()
            if current_char == '.':
                if num == 0:
                    return dfs()
                else:
                    return [num] + dfs()
            else:
                pass
            return None

        elif current_char == '_':
            if executor.stack and executor.stack[-1] == 0:
                executor.direction = [1, 0]
            else:
                executor.direction = [-1, 0]
            success = executor.execute_step()
            if success:
                return dfs()
            else:
                return None

        elif current_char == '|':
            if executor.stack and executor.stack[-1] == 0:
                executor.direction = [0, -1]
            else:
                executor.direction = [0, 1]
            success = executor.execute_step()
            if success:
                return dfs()
            else:
                return None

        elif current_char == ' ':
            success = executor.execute_step()
            if success:
                return dfs()
            else:
                return None

        else:
            return None

    result = dfs()
    if result is None:
        return "false"
    return result

