                '/': lambda a, b: a // b if b != 0 else None,
                '=': lambda a, b: 1 if a == b else 0,
                '<': lambda a, b: 1 if a < b else 0,
                '>': lambda a, b: 1 if a > b else 0,
                '!': lambda a, b: 0 if a == b else 1,
