import sys
import re

def parse_golorp(name):
    # Extract the pattern from the name
    pattern = re.search(r'\?([^:]+):([^,]+)', name).groups()
    jaws_pattern, stomach_pattern = pattern

    # Parse the jaws pattern to get the number of variables and their constraints
    jaws = []
    i = 0
    while i < len(jaws_pattern):
        if jaws_pattern[i] == '_':
            # Skip underscores as they are not part of the pattern
            i += 1
        elif jaws_pattern[i] == '<':
            # '<' indicates a constraint between two variables
            j = i + 1
            while j < len(jaws_pattern) and jaws_pattern[j] != '>':
                j += 1
            if j >= len(jaws_pattern):
                return None, None
            var1 = int(jaws_pattern[i+1:j])
            var2 = int(jaws_pattern[j+1:])
            jaws.append((var1, var2))
            i = j + 1
        else:
            # Assume it's a variable index
            var = int(jaws_pattern[i])
            jaws.append(var)
            i += 1

    # Parse the stomach pattern to get the number of variables
    stomach_vars = []
    i = 0
    while i < len(stomach_pattern):
        if stomach_pattern[i] == '_':
            i += 1
        elif stomach_pattern[i] == '<':
            j = i + 1
            while j < len(stomach_pattern) and stomach_pattern[j] != '>':
                j += 1
            if j >= len(stomach_pattern):
                return None, None
            var = int(stomach_pattern[i+1:j])
            stomach_vars.append(var)
            i = j + 1
        else:
            var = int(stomach_pattern[i])
            stomach_vars.append(var)
            i += 1

    return jaws, stomach_vars

def is_valid_sequence(jaws, sequence):
    # Map variable indices to values
    var_values = {i: sequence[i] for i in range(len(sequence))}

    # Check all constraints
    for a, b in jaws:
        if var_values[a] >= var_values[b]:
            return False
    return True

def find_lex_smallest_sequence(jaws, num_vars):
    # Generate all possible sequences of length num_vars with digits 0-9
    # We use BFS to find the lexicographically smallest valid sequence
    from collections import deque

    queue = deque()
    queue.append('')

    visited = set()

    while queue:
        seq = queue.popleft()
        if len(seq) == num_vars:
            if is_valid_sequence(jaws, seq):
                return seq
            continue

        for digit in '0123456789':
            if len(seq) > 0 and seq[-1] > digit:
                continue  # Skip to maintain lex order
            new_seq = seq + digit
            if new_seq not in visited:
                visited.add(new_seq)
                queue.append(new_seq)

    return None

def main():
    input_str = sys.stdin.read().strip()
    jaws, stomach_vars = parse_golorp(input_str)
    if not jaws or not stomach_vars:
        print("false")
        return

    num_vars = max(max(j) for j in jaws)
    if num_vars != len(stomach_vars):
        print("false")
        return

    result = find_lex_smallest_sequence(jaws, num_vars)
    if result:
        print(result)
    else:
        print("false")

