        rules = list(s_str)
        rules.reverse()

        while left < right:
            mid = (left + right) // 2
            left_count = mid - left + 1
            right_count = right - mid

            if left_count > 1:
                l_winners = set(range(mid - left + 1))
                l_winners.remove(x)
                if not can_win_subtree(l_winners, rules[:mid - left + 1]):
