                r_winners = set(range(mid + 1, right + 1))
                r_winners.remove(x)
                if not can_win_subtree(r_winners, rules[:right_count]):
                    return False
            left = mid + 1
        return True

    def can_win_subtree(winners_set, rules_phase):
        if not winners_set:
            return True
        if len(winners_set) == 1:
            return True

        rule_char = rules_phase[0]
        winners_list = sorted(winners_set)
        if rule_char == '1':
            winner = max(winners_list)
            winners_set_new = set(winners_list)
            winners_set_new.remove(winner)
