    region_id = 0
    for i in range(len(roads)):
        for j in range(i + 1, len(roads)):
            p1 = line_intersection(roads[i], roads[j])
            if p1 is not None and not is_point_on_line(x, y, roads[i][0], roads[i][1], roads[i][2]):
                region_id ^= 1
    return region_id

