    n = int(input().strip())
    arr = list(map(int, input().split()))

    if n == 0:
        print(0)
        return

    total_ones = sum(arr)
    total_twos = n - total_ones

    if total_ones == 0 or total_twos == 0:
        print(n)
        return

    left_ones = [0] * n
    right_twos = [0] * n

    for i in range(n):
        if i == 0:
            left_ones[i] = 1 if arr[i] == 1 else 0
        else:
            left_ones[i] = left_ones[i-1] + (1 if arr[i] == 1 else 0)

    for i in range(n-1, -1, -1):
        if i == n-1:
            right_twos[i] = 1 if arr[i] == 2 else 0
        else:
            right_twos[i] = right_twos[i+1] + (1 if arr[i] == 2 else 0)

    best = 0
    for i in range(n):
        for j in range(i, n):
            ones_left = left_ones[i-1] if i > 0 else 0
            twos_right = right_twos[j+1] if j < n-1 else 0

            ones_in_interval = left_ones[j] - (left_ones[i-1] if i-1 >= 0 else 0)
            twos_in_interval = (total_twos - (right_twos[i-1] if i-1 >= 0 else 0)) - (total_twos - (right_twos[j])) if j < n-1 else (total_twos - (right_twos[i-1] if i-1 >= 0 else 0))

            current_ones = ones_left + ones_in_interval
            current_twos = twos_right + twos_in_interval
            total_length = current_ones + current_twos
            if total_length > best:
                best = total_length

    if best == 0:
        best = total_ones + total_twos

