def comb(n, k):
    if k < 0 or k > n:
        return 0
    return math.comb(n, k)

def main():
    import sys
    input = sys.stdin.read
    n, m, g = map(int, input().split())

    total_length = n + m
    total_length_after_modifications = total_length - (total_length - 2) // 2

    # We need to end up with a string of length 1
    if total_length_after_modifications != 1:
        print(0)
        return

    # The final character must be g
    if total_length_after_modifications == 1:
        if g == 0:
            # We need to have exactly n zeros and m ones in the original string
            # and after some modifications, we end up with one zero
            # So the number of zeros in the original string must be equal to the number of ones plus 1
            if n == m + 1:
                # Count the number of ways to arrange n zeros and m ones
                print(comb(n + m, m) % MOD)
            else:
                print(0)
        else:
            # Similarly for g == 1
            if m == n + 1:
                print(comb(n + m, m) % MOD)
            else:
                print(0)
    else:
        print(0)

