            exp1 = 10**(n_val - hc - 1)
            next_val, _ = rec(pos+1, hc+1, mc)
            if next_val != NEG_INF:
                candidate1 = int(A_str[pos]) * exp1 + next_val
                res1 = candidate1

        if mc < n_val:
            exp2 = 10**(n_val - mc - 1)
            next_val, _ = rec(pos+1, hc, mc+1)
            if next_val != NEG_INF:
                candidate2 = int(A_str[pos]) * exp2 + next_val
                res2 = candidate2

        best_val = NEG_INF
        choice = None

        if res1 > NEG_INF and res2 > NEG_INF:
            best_val = max(res1, res2)
            choice = 'H' if res1 >= res2 else 'M'
        elif res1 > NEG_INF:
            best_val = res1
            choice = 'H'
        elif res2 > NEG_INF:
            best_val = res2
            choice = 'M'
        else:
            best_val = NEG_INF
            choice = None

        dp_table[key] = (best_val, choice)
        return (best_val, choice)

