import sys
import functools

def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    n = int(data[0])
    A = data[1]

    @functools.lru_cache(maxsize=None)
    def dp(pos, h_moves, m_moves, h_num, m_num):
        if pos == 2 * n:
            return (h_num, m_num)
        if h_moves == n and m_moves == n:
            return (h_num, m_num)

        digit = A[pos]
        if h_moves < n:
            h_val = int(digit) * (10 ** (n - h_moves - 1)) + h_num
            res_h, res_m = dp(pos + 1, h_moves + 1, m_moves, h_val, m_num)
            option1 = (res_h, res_m)
        else:
            option1 = (-1, -1)

        if m_moves < n:
            m_val = int(digit) * (10 ** (n - m_moves - 1)) + m_num
            res_h, res_m = dp(pos + 1, h_moves, m_moves + 1, h_num, m_val)
            option2 = (res_h, res_m)
        else:
            option2 = (-1, -1)

        if option1[0] > option2[0]:
            return option1
        elif option2[0] > option1[0]:
            return option2
        else:
            # Choose the one with more 'H' to break ties
            if option1[1] > option2[1]:
                return option1
            else:
                return option2

    @functools.lru_cache(maxsize=None)
    def dp_with_path(pos, h_moves, m_moves, h_num, m_num):
        if pos == 2 * n:
            return ('', h_num, m_num)
        if h_moves == n and m_moves == n:
            return ('', h_num, m_num)

        digit = A[pos]
        if h_moves < n:
            h_val = int(digit) * (10 ** (n - h_moves - 1)) + h_num
            path_h, res_h, res_m = dp_with_path(pos + 1, h_moves + 1, m_moves, h_val, m_num)
            option1 = ('H' + path_h, res_h, res_m)
        else:
            option1 = ('', -1, -1)

        if m_moves < n:
            m_val = int(digit) * (10 ** (n - m_moves - 1)) + m_num
            path_m, res_h, res_m = dp_with_path(pos + 1, h_moves, m_moves + 1, h_num, m_val)
            option2 = ('M' + path_m, res_h, res_m)
        else:
            option2 = ('', -1, -1)

        if option1[1] > option2[1]:
            return option1
        elif option2[1] > option1[1]:
            return option2
        else:
            # Choose the one with more 'H' to break ties
            if option1[0].count('H') > option2[0].count('H'):
                return option1
            else:
                return option2

    _, _, _ = dp(0, 0, 0, 0, 0)
    path, _, _ = dp_with_path(0, 0, 0, 0, 0)
    print(path)

