    for i in range(max_n, 0, -1):
        inv_fact[i - 1] = inv_fact[i] * i % mod

    def nCr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[r] % mod * inv_fact[n_val - r] % mod

    dp = [0] * (n + 1)
    dp[0] = 1

    for i in range(1, n + 1):
        total = 0
        for j in range(0, i):
            if j == 0:
                term = nCr(i - 1, 1)
            else:
                term = nCr(i - j - 1, 1)
            total = (total + term * dp[j]) % mod
        dp[i] = total

    total_ways = 0
    for i in range(1, n + 1):
        if i == 1:
            ways_here = 1
        else:
            ways_here = nCr(i - 1, 1)
        if dp[i] != 0:
            total_ways = (total_ways + ways_here * dp[i]) % mod

    total_ways = total_ways * pow(pow(2, n, mod), mod - 2, mod) % mod
    print(total_ways)

