def main():
    import sys
    data = sys.stdin.read().split()
    if not data:
        return

    n = int(data[0])

    if n == 1:
        print("1")
        return

    board = [[0] * n for _ in range(n)]
    visited = [[False] * n for _ in range(n)]
    directions_rook = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    directions_queen = directions_rook + [(1, 1), (1, -1), (-1, 1), (-1, -1)]

    def get_moves(x, y, directions):
        moves = []
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            while 0 <= nx < n and 0 <= ny < n:
                if not visited[nx][ny]:
                    moves.append((nx, ny))
                nx += dx
                ny += dy
        return moves

    def solve_for_rook_and_queen():
        if n % 2 == 0:
            num_blocks = (n // 2) ** 2
            total_nums_per_block = 4
            base = 1

            for block_idx in range(num_blocks):
                block_row = (block_idx // (n//2)) * (n//2)
                block_col = (block_idx % (n//2)) * (n//2)

                nums = [base + i for i in range(total_nums_per_block)]
                base += total_nums_per_block

                board[block_row][block_col] = nums[0]
                board[block_row][block_col+1] = nums[1]
                board[block_row+1][block_col] = nums[2]
                board[block_row+1][block_col+1] = nums[3]

            for i in range(n):
                for j in range(n):
                    if board[i][j] == 0:
                        idx = n*n - (n*n - 1 - (i * n + j))
                        board[i][j] = idx + 1
        else:
            num_blocks = ((n-1)//2) ** 2
            total_nums_per_block = 4
            base = 1

            for block_idx in range(num_blocks):
                block_row = (block_idx // ((n-1)//2)) * ((n-1)//2)
                block_col = (block_idx % ((n-1)//2)) * ((n-1)//2)

                nums = [base + i for i in range(total_nums_per_block)]
                base += total_nums_per_block

                board[block_row][block_col] = nums[0]
                board[block_row][block_col+1] = nums[1]
                board[block_row+1][block_col] = nums[2]
                board[block_row+1][block_col+1] = nums[3]

            mid = n//2
            for i in range(mid):
                if i < (n-1)//2:
                    board[mid][i] = base + 2*(mid) + 1
                    board[mid][i+1] = base + 2*(mid) + 2
                else:
                    board[mid][i] = base + 2*(mid) + 2*i - 2*mid + 1
                    board[mid][i+1] = base + 2*(mid) + 2*i - 2*mid + 2

            base += 2*mid + 2
            for i in range(n):
                for j in range(n):
                    if board[i][j] == 0:
                        idx = n*n - (n*n - 1 - (i * n + j))
                        board[i][j] = idx + 1

    solve_for_rook_and_queen()

    for i in range(n):
        print(' '.join(map(str, board[i])))

