    for i in range(1, n+1):
        for inv_count in range(max_inv+1):
            if dp[i-1][inv_count] == 0:
                continue
            for j in range(1, i+1):
                new_inv = inv_count + j - 1
                if new_inv <= max_inv:
                    dp[i][new_inv] = (dp[i][new_inv] + dp[i-1][inv_count]) % mod

    f = [0]*(max_inv+1)
    for inv_count in range(max_inv+1):
        f[inv_count] = dp[n][inv_count]

    total_permutations = sum(f) % mod
    total_pairs = (total_permutations * (total_permutations - 1)) % mod
    total_lex_ordered_pairs = total_pairs * pow(2, mod-2, mod) % mod if mod > 1 else total_pairs // 2

    C1 = 0
    for k in range(max_inv+1):
        cnt = f[k]
        if cnt == 0:
            continue
        C1 = (C1 + cnt * (cnt - 1)) % mod
    C1 = C1 * pow(2, mod-2, mod) % mod if mod > 1 else C1 // 2

    A_plus_B = (total_lex_ordered_pairs - C1) % mod

    total_A_and_B = 0
    for k in range(max_inv+1):
        for l in range(max_inv+1):
            if k == l:
