def main():
    n = int(input().strip())

    def find_winner_games(n):
        if n <= 1:
            return 0
        if n == 2:
            return 1
        if n == 3:
            return 2
        exp_val = n.bit_length() - 1
        total_players = (1 << exp_val)
        if n == total_players:
            return exp_val + 1
        else:
            return find_winner_games(total_players) + find_winner_games(n - total_players)

    result = find_winner_games(n)
    print(result)

