import math

def find_optimal_k(a, b):
    if a == b:
        return 0

    g = math.gcd(a, b)
    d = abs(a - b)
    min_lcm = float('inf')
    best_k = 0

    # Try all divisors of d up to sqrt(d)
    for i in range(1, int(math.isqrt(d)) + 1):
        if d % i == 0:
            # i is a divisor
            k = (d // i) - b
            if k >= 0:
                lcm = (a + k) * (b + k) // math.gcd(a + k, b + k)
                if lcm < min_lcm or (lcm == min_lcm and k < best_k):
                    min_lcm = lcm
                    best_k = k
            # d//i is another divisor
            k = (d // i) - a
            if k >= 0:
                lcm = (a + k) * (b + k) // math.gcd(a + k, b + k)
                if lcm < min_lcm or (lcm == min_lcm and k < best_k):
                    min_lcm = lcm
                    best_k = k

    return best_k

