def main():
    data = sys.stdin.read().split()
    if not data:
        print("NO")
        return

    n = int(data[0])
    m = int(data[1])
    edges = []
    index = 2
    for i in range(m):
        u = int(data[index]); v = int(data[index+1]); g = int(data[index+2]); l = int(data[index+3])
        index += 4
        edges.append((u-1, v-1, g, l))

    graph = [[] for _ in range(n)]
    for u, v, g, l in edges:
        graph[u].append((v, g, l))
        graph[v].append((u, g, l))

    parent_map = {}
    visited = [False] * n
    components = []
    comp_nodes = []
    for i in range(n):
        if not visited[i]:
            stack = [i]
            visited[i] = True
            comp = []
            while stack:
                node = stack.pop()
                comp.append(node)
                for neighbor, g, l in graph[node]:
                    if not visited[neighbor]:
                        visited[neighbor] = True
                        stack.append(neighbor)
            components.append(comp)

    ans = [-1] * n
    valid = True
    for comp in components:
        comp_graph = {}
        for node in comp:
            comp_graph[node] = []
            for neighbor, g, l in graph[node]:
                if neighbor in comp:
                    comp_graph[node].append((neighbor, g, l))

        for u in comp:
            for v, g, l in comp_graph[u]:
                if l % g != 0:
                    valid = False
                    break

            if not valid:
                break

        if not valid:
            break

        start = comp[0]
        ans[start] = 1
        queue = deque([start])
        while queue and valid:
            node = queue.popleft()
            for neighbor, g, l in comp_graph[node]:
                if ans[neighbor] == -1:
                    ans[neighbor] = ans[node] * (l // (g * ans[node]))
                    queue.append(neighbor)

        if not valid:
            break

        for u in comp:
            for v, g, l in comp_graph[u]:
                if ans[u] == -1 or ans[v] == -1:
                    continue
                if (ans[u] * ans[v]) != (g * l):
                    valid = False
                    break
                if g != math.gcd(ans[u], ans[v]):
                    valid = False
                    break

        if not valid:
            break

    if valid:
        print("YES")
        print(" ".join(map(str, ans)))
    else:
        print("NO")

