def main():
    import sys
    input = sys.stdin.read().split()
    n = int(input[0])

    if n % 2 != 0:
        print(0)
        return

    if n == 2:
        print(-1 % MOD)
        return

    # Precompute powers and factorials up to n for efficiency
    max_n = n
    fact = [1] * (max_n + 1)
    inv_fact = [1] * (max_n + 1)
    for i in range(1, max_n + 1):
        fact[i] = fact[i - 1] * i % MOD

    inv_fact[max_n] = pow(fact[max_n], MOD - 2, MOD)
    for i in range(max_n, 0, -1):
        inv_fact[i - 1] = inv_fact[i] * i % MOD

    def nCr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[r] % MOD * inv_fact[n_val - r] % MOD

    def power_func(x, s):
        if s == 0:
            return 1
        return pow(x, s - 2, MOD)

    total = 0
    half = (n - 2) // 2

    for k in range(half + 1):
        if k == 0:
            term1 = 1
        else:
            term1 = nCr(n - 2, k) * power_func(k + 1, k) % MOD

        if n - 2 - k == 0:
            term2 = 1
        else:
            term2 = nCr(n - 2, k) * power_func(n - 1 - k, n - 3 - k) % MOD

        total = (total + term1 + term2) % MOD

    total = (total * pow(n, n - 4, MOD)) % MOD
    total = (total * pow(2, n - 3, MOD)) % MOD

    print(total)

