        return

    inv2 = pow(2, MOD-2, MOD)
    fact = [1] * (n + 1)
    inv_fact = [1] * (n + 1)

    for i in range(1, n + 1):
        fact[i] = fact[i - 1] * i % MOD

    inv_fact[n] = pow(fact[n], MOD - 2, MOD)
    for i in range(n, 0, -1):
        inv_fact[i - 1] = inv_fact[i] * i % MOD

    def nCr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val] * inv_fact[r] % MOD * inv_fact[n_val - r] % MOD

    def nHr(n_val, r):
        if r < 0 or r > n_val:
            return 0
        return fact[n_val + r] * inv_fact[r] % MOD * inv_fact[n_val] % MOD

    def nT(n_val):
        if n_val <= 1:
            return 1
        return pow(n_val, n_val - 2, MOD)

    def count_trees_with_edge_on_path(edge_length):
        total = 0
        for k in range(1, edge_length):
            count1 = nCr(edge_length, k) * nT(k) % MOD
            count2 = nCr(edge_length, k) * nT(edge_length - k) % MOD
            total = (total + count1 * count2) % MOD
        return total

    total_sum = 0
    for k in range(2, n + 1):
        edge_length = k
        ways = count_trees_with_edge_on_path(edge_length)
        num_edges = nCr(n, k) * nCr(n - k, 1) % MOD
        total_sum = (total_sum + ways * num_edges) % MOD

    total_sum = total_sum * pow(2, n - 1, MOD) % MOD
    total_sum = total_sum * pow(2, MOD - 2, MOD) % MOD

