def main():
    import sys
    data = sys.stdin.read().splitlines()
    n = int(data[0].strip())
    A = data[1].strip()
    B = data[2].strip()

    def outcome(a, b):
        if a == b:
            return 0
        elif (a, b) in [('R', 'S'), ('S', 'P'), ('P', 'R')]:
            return 1  # Polycarpus gets a red spot (since Nikephoros wins)
        else:
            return -1  # Nikephoros gets a red spot (Polycarpus wins)

    m = len(A)
    k = len(B)
    total_red_nike = 0
    total_red_poly = 0

    for i in range(m):
        for j in range(k):
            a_char = A[i]
            b_char = B[j]
            res = outcome(a_char, b_char)
            if res == 1:
                poly_count = 0
                while n >= 0:
                    if (i * (k + poly_count)) % m == i and (j * (m + poly_count)) % k == j:
                        break
                    poly_count += 1
                cycles = (n) // poly_count
                rem = n % poly_count
                total_red_poly += cycles
                for r in range(rem + 1):
                    if outcome(A[(i + r) % m], B[(j + r) % k]) == 1:
                        total_red_poly += 1
            elif res == -1:
                nike_count = 0
                while n >= 0:
                    if (i * (k + nike_count)) % m == i and (j * (m + nike_count)) % k == j:
                        break
                    nike_count += 1
                cycles = (n) // nike_count
                rem = n % nike_count
                total_red_nike += cycles
                for r in range(rem + 1):
                    if outcome(A[(i + r) % m], B[(j + r) % k]) == -1:
                        total_red_nike += 1

    print(f"{total_red_nike} {total_red_poly}")

if __name__ == '__main__':
    main()

import math

def main():
    n = int(input().strip())
    A_str = input().strip()
    B_str = input().strip()

    m = len(A_str)
    k = len(B_str)

    if m == 0 or k == 0:
        print("0 0")
        return

    L = (m * k) // math.gcd(m, k)

    count_Nik_loss_cycle = 0
    count_Pol_loss_cycle = 0

    for i in range(L):
        a_char = A_str[i % m]
        b_char = B_str[i % k]

        if (a_char, b_char) == ('R', 'S'):
            count_Nik_loss_cycle += 1
        elif (a_char, b_char) == ('S', 'P'):
            count_Nik_loss_cycle += 1
        elif (a_char, b_char) == ('P', 'R'):
            count_Nik_loss_cycle += 1

        if (a_char, b_char) == ('S', 'R'):
            count_Pol_loss_cycle += 1
        elif (a_char, b_char) == ('R', 'P'):
            count_Pol_loss_cycle += 1
        elif (a_char, b_char) == ('P', 'S'):
            count_Pol_loss_cycle += 1

    cycles = n // L
    remainder = n % L

    total_Nik_loss = cycles * count_Nik_loss_cycle
    total_Pol_loss = cycles * count_Pol_loss_cycle

    for i in range(remainder):
        a_char = A_str[i % m]
        b_char = B_str[i % k]

        if (a_char, b_char) == ('R', 'S'):
            total_Nik_loss += 1
