def main():
    import sys
    data = sys.stdin.read().splitlines()
    n = int(data[0].strip())
    A_str = data[1].strip()
    B_str = data[2].strip()

    m = len(A_str)
    k = len(B_str)

    if m == 0 or k == 0:
        print("0 0")
        return

    T = (m * k) // math.gcd(m, k)

    total_cycles = n // T
    remainder = n % T

    def get_winner(a, b):
        if a == b:
            return 0
        if (a, b) in [('R', 'S'), ('S', 'P'), ('P', 'R')]:
            return 1  # Polycarpus wins (Nikephoros loses)
        else:
            return -1  # Nikephoros wins (Polycarpus loses)

    nike_red = 0
    poly_red = 0

    for i in range(n):
        idx_a = i % m
        idx_b = i % k
        res = get_winner(A_str[idx_a], B_str[idx_b])
        if res == 1:
            poly_red += 1
        elif res == -1:
            nike_red += 1

    nike_red_total = nike_red * total_cycles
    poly_red_total = poly_red * total_cycles

    for i in range(remainder):
        idx_a = i % m
        idx_b = i % k
        res = get_winner(A_str[idx_a], B_str[idx_b])
        if res == 1:
            poly_red_total += 1
        elif res == -1:
            nike_red_total += 1

    print(f"{nike_red_total} {poly_red_total}")

