    def build_structure_by_l(vouchers_list):
        sorted_list = sorted(vouchers_list, key=lambda x: x[0])
        min_cost_from_right = [0] * len(sorted_list)
        min_so_far = float('inf')
        idx = len(sorted_list) - 1
        while idx >= 0:
            if sorted_list[idx][2] < min_so_far:
                min_so_far = sorted_list[idx][2]
            min_cost_from_right[idx] = min_so_far
            idx -= 1
        return sorted_list, min_cost_from_right

    def build_structure_by_r(vouchers_list):
        sorted_list = sorted(vouchers_list, key=lambda x: x[1])
        min_cost_from_left = [0] * len(sorted_list)
        min_so_far = float('inf')
        idx = 0
        while idx < len(sorted_list):
            if sorted_list[idx][2] < min_so_far:
                min_so_far = sorted_list[idx][2]
            min_cost_from_left[idx] = min_so_far
            idx += 1
        return sorted_list, min_cost_from_left

    structures_by_l = {}
    structures_by_r = {}
    for d in groups:
        lst1, arr1 = build_structure_by_l(groups[d])
        lst2, arr2 = build_structure_by_r(groups[d])
        structures_by_l[d] = (lst1, arr1)
        structures_by_r[d] = (lst2, arr2)

    def query_by_l(sorted_list, min_arr, threshold):
        low = 0
        high = len(sorted_list) - 1
        ans_index = len(sorted_list)
        while low <= high:
            mid = (low + high) // 2
            if sorted_list[mid][0] > threshold:
                ans_index = mid
                high = mid - 1
            else:
                low = mid + 1
        if ans_index == len(sorted_list):
            return None
        else:
            return min_arr[ans_index]

    def query_by_r(sorted_list, min_arr, threshold):
        low = 0
        high = len(sorted_list) - 1
        ans_index = -1
        while low <= high:
            mid = (low + high) // 2
            if sorted_list[mid][1] < threshold:
                ans_index = mid
                low = mid + 1
            else:
                high = mid - 1
        if ans_index == -1:
            return None
        else:
            return min_arr[ans_index]

    INF = 10**18
    best_ans = INF

    for (l, r, cost_val) in vouchers:
        d_i = r - l + 1
        d_j = x - d_i
        if d_j not in structures_by_l:
            continue

        cand1 = None
        cand2 = None

        lst1, min_arr1 = structures_by_l[d_j]
        if query_by_l(lst1, min_arr1, r) is not None:
            cand1 = query_by_l(lst1, min_arr1, r)

        lst2, min_arr2 = structures_by_r[d_j]
        if query_by_r(lst2, min_arr2, l) is not None:
            cand2 = query_by_r(lst2, min_arr2, l)

        if cand1 is not None and cand2 is not None:
            total = cost_val + min(cand1, cand2)
        elif cand1 is not None:
            total = cost_val + cand1
        elif cand2 is not None:
            total = cost_val + cand2
        else:
            continue

        if total < best_ans:
            best_ans = total

    if best_ans == INF:
        print(-1)
    else:
        print(best_ans)

