import sys
from collections import defaultdict

def main():
    data = sys.stdin.read().split()
    it = iter(data)
    n = int(next(it))
    x = int(next(it))
    vouchers = []
    for _ in range(n):
        l = int(next(it))
        r = int(next(it))
        cost = int(next(it))
        vouchers.append((l, r, cost))

    groups = defaultdict(list)
    max_day = 200000
    for l, r, cost in vouchers:
        d = r - l + 1
        groups[d].append((l, r, cost))

    group_by_duration = {}
    for d, lst in groups.items():
        sorted_lst = sorted(lst, key=lambda x: x[0])
        l_list = [v[0] for v in sorted_lst]
        costs = [v[2] for v in sorted_lst]
        min_suffix = [0] * len(sorted_lst)
        if sorted_lst:
            min_suffix[-1] = costs[-1]
            for i in range(len(sorted_lst)-2, -1, -1):
                min_suffix[i] = min(min_suffix[i+1], costs[i])
        group_by_duration[d] = {
            'sorted_vouchers': sorted_lst,
            'l_list': l_list,
            'min_suffix': min_suffix
        }

    ans = float('inf')
    found = False

    def query_group(d, threshold, is_case1=True):
        if d not in group_by_duration:
            return None
        data = group_by_duration[d]
        sorted_vouchers = data['sorted_vouchers']
        l_list = data['l_list']
        min_suffix = data['min_suffix']
        if not sorted_vouchers:
            return None
        pos = bisect.bisect_right(l_list, threshold)
        if pos >= len(sorted_vouchers):
            return None
        return min_suffix[pos]

    def process_case1(d, d2):
        nonlocal ans, found
        if d2 not in group_by_duration:
            return
        group_d = group_by_duration[d]['sorted_vouchers']
        group_d2 = group_by_duration[d2]['sorted_vouchers']
        if not group_d or not group_d2:
            return
        for l1, r1, cost1 in group_d:
            R = r1
            min_cost_d2 = query_group(d2, R, True)
            if min_cost_d2 is not None:
                total_cost = cost1 + min_cost_d2
                if total_cost < ans:
                    ans = total_cost
                    found = True

    def process_case2(d, d2):
        nonlocal ans, found
        if d2 not in group_by_duration:
            return
        group_d2 = group_by_duration[d2]['sorted_vouchers']
        group_d = group_by_duration[d]['sorted_vouchers']
        if not group_d or not group_d2:
            return
        for l1, r1, cost1 in group_d2:
            R = r1
            min_cost_d = query_group(d, R, False)
            if min_cost_d is not None:
                total_cost = cost1 + min_cost_d
                if total_cost < ans:
                    ans = total_cost
                    found = True

    for d_val in list(group_by_duration.keys()):
        d2_val = x - d_val
        if d_val == d2_val:
            if d_val in group_by_duration:
                group1 = group_by_duration[d_val]['sorted_vouchers']
                if len(group1) >= 2:
                    for i in range(len(group1)):
                        for j in range(i+1, len(group1)):
                            l1, r1, c1 = group1[i]
                            l2, r2, c2 = group1[j]
                            if r1 < l2 or r2 < l1:
                                total_cost = c1 + c2
                                if total_cost < ans:
                                    ans = total_cost
                                    found = True
        else:
            if d2_val in group_by_duration:
                process_case1(d_val, d2_val)
                process_case2(d_val, d2_val)

    print(ans if found else -1)

