def is_perfect_power(x, t):
    low = 2
    high = x
    while low <= high:
        mid = (low + high) // 2
        power = mid ** t
        if power == x:
            return mid
        elif power < x:
            low = mid + 1
        else:
            high = mid - 1
    return None

