import heapq

def main():
    data = sys.stdin.read().split()
    if not data:
        return

    it = iter(data)
    n = int(next(it)); m = int(next(it))

    edges = []
    for _ in range(m):
        a = int(next(it)); b = int(next(it)); c = int(next(it))
        edges.append((a, b, c))

    graph = [[] for _ in range(n+1)]
    for i, (a, b, c) in enumerate(edges):
        graph[a].append((b, c, i))
        graph[b].append((a, c, i))

    flow_balance = [0] * (n+1)
    for a, b, c in edges:
        if a == 1:
            flow_balance[b] -= c
        elif b == 1:
            flow_balance[a] += c
        else:
            flow_balance[a] += c
            flow_balance[b] -= c

    ans = [-1] * m
    pq = []
    for i in range(1, n+1):
        if flow_balance[i] > 0:
            heapq.heappush(pq, (-flow_balance[i], i))
        elif flow_balance[i] < 0:
            flow_balance[i] = -flow_balance[i]
            heapq.heappush(pq, (flow_balance[i], i))

    while pq:
        neg_count, node = heapq.heappop(pq)
        count = neg_count if node != 1 else float('inf')
        for neighbor, capacity, idx in graph[node]:
            if node == 1:
                if flow_balance[neighbor] < 0:
                    continue
                if ans[idx] != 0:
                    if flow_balance[neighbor] <= 0:
                        ans[idx] = 0
                        flow_balance[neighbor] -= capacity
                        pq_key = (flow_balance[neighbor], neighbor) if flow_balance[neighbor] < 0 else (0, neighbor)
                        heapq.heappush(pq, pq_key)
            else:
                if flow_balance[node] == 0:
                    break
                if flow_balance[neighbor] >= 0:
                    if ans[idx] == -1:
                        ans[idx] = 1
                        flow_balance[node] -= capacity
                        flow_balance[neighbor] += capacity
                        pq_key = (flow_balance[neighbor], neighbor) if flow_balance[neighbor] > 0 else (0, neighbor)
                        heapq.heappush(pq, pq_key)
                    elif ans[idx] == 0:
                        pass
                else:
                    if ans[idx] == -1:
                        ans[idx] = 0
                        flow_balance[node] += capacity
                        flow_balance[neighbor] -= capacity
                        pq_key = (flow_balance[neighbor], neighbor) if flow_balance[neighbor] < 0 else (0, neighbor)
                        heapq.heappush(pq, pq_key)
                    elif ans[idx] == 1:
                        pass

    for i in range(m):
        print(ans[i])

