def main():
    import sys
    data = sys.stdin.read().splitlines()
    if not data:
        return

    results = []
    for line in data:
        s = line.strip()
        if not s:
            continue

        n = len(s)
        ptr_x, ptr_y = 0, 0
        stack = []
        visited = set()
        constraints = []

        def get_ptr():
            nonlocal ptr_x, ptr_y
            while True:
                pos = (ptr_x, ptr_y)
                if pos in visited:
                    return None
                visited.add(pos)
                if ptr_x >= n or ptr_x < 0 or ptr_y < 0 or ptr_y >= n:
                    return None
                c = s[ptr_y][ptr_x]
                if c == '>':
                    ptr_x += 1
                elif c == '<':
                    ptr_x -= 1
                elif c == 'v':
                    ptr_y += 1
                elif c == '^':
                    ptr_y -= 1
                elif c == '_':
                    if stack:
                        prev_val = stack[-1]
                    else:
                        prev_val = 0
                    if prev_val == 0:
                        ptr_x += 1
                    else:
                        ptr_x -= 1
                elif c == '|':
                    if stack:
                        prev_val = stack[-1]
                    else:
                        prev_val = 0
                    if prev_val == 0:
                        ptr_y += 1
                    else:
                        ptr_y -= 1
                elif c == '?':
                    ptr_x += 1
                elif c == '!':
                    pass
                elif c == '.' or c == ',':
                    if stack:
                        stack.pop()
                elif c == '$':
                    if stack:
                        stack.pop()
                elif c == ':':
                    if stack:
                        a = stack.pop()
                        b = stack.pop()
                        stack.append(a)
                        stack.append(b)
                elif c == '+':
                    if stack:
                        a = stack.pop()
                        b = stack.pop()
                        stack.append((a + b) % 10)
                elif c == '-':
                    if stack:
                        a = stack.pop()
                        b = stack.pop()
                        stack.append((b - a) % 10)
                elif c == '*':
                    if stack:
                        a = stack.pop()
                        b = stack.pop()
                        stack.append((a * b) % 10)
                elif c == '/':
                    if stack:
                        a = stack.pop()
                        b = stack.pop()
                        stack.append(1 if b != 0 else 0)
                elif c == '%':
                    if stack:
                        a = stack.pop()
                        b = stack.pop()
                        stack.append(b % a if a != 0 else 0)
                elif c == '<':
                    ptr_x -= 1
                elif c == '>':
                    ptr_x += 1
                elif c == 'v':
                    ptr_y += 1
                elif c == '^':
                    ptr_y -= 1
                elif c == '_':
                    if stack:
                        prev_val = stack[-1]
                    else:
                        prev_val = 0
                    if prev_val == 0:
                        ptr_x += 1
                    else:
                        ptr_x -= 1
                elif c == '|':
                    if stack:
                        prev_val = stack[-1]
                    else:
                        prev_val = 0
                    if prev_val == 0:
                        ptr_y += 1
                    else:
                        ptr_y -= 1
                else:
                    break
                if ptr_x < 0 or ptr_x >= n or ptr_y < 0 or ptr_y >= n:
                    return None
            return ptr_x, ptr_y

        while True:
            next_pos = get_ptr()
            if next_pos is None:
                break

        if stack:
            num_vars = len(stack)
        else:
            num_vars = 0

        if num_vars == 0:
            results.append("false")
            continue

        constraints_dict = {}
        for i in range(num_vars):
            if i not in constraints_dict:
                constraints_dict[i] = []

        for constr in constraints:
            parts = constr.split(',')
            if len(parts) != 2:
                continue
            a, b = parts[0], parts[1]
            if '>' in a:
                idx1, op, idx2 = a.split('[')
                idx1 = int(idx1.strip())
                idx2 = int(idx2.strip())
                if op == '>':
                    constraints_dict[idx1].append((idx2, '>'))
            elif '>' in b:
                idx1, op, idx2 = b.split('[')
                idx1 = int(idx1.strip())
                idx2 = int(idx2.strip())
                if op == '>':
                    constraints_dict[idx2].append((idx1, '<'))
            elif '<' in a:
                idx1, op, idx2 = a.split('[')
                idx1 = int(idx1.strip())
                idx2 = int(idx2.strip())
                if op == '<':
                    constraints_dict[idx1].append((idx2, '<'))
            elif '<' in b:
                idx1, op, idx2 = b.split('[')
                idx1 = int(idx1.strip())
                idx2 = int(idx2.strip())
                if op == '<':
                    constraints_dict[idx2].append((idx1, '>'))

        var_sequence = [0] * num_vars

        def backtrack(idx):
            if idx == num_vars:
                return True
            for d in range(0, 10):
                var_sequence[idx] = d
                for constraint in constraints_dict.get(idx, []):
                    target_idx, op = constraint
                    if target_idx >= num_vars or target_idx < 0:
                        continue
                    if op == '>':
                        if var_sequence[target_idx] <= var_sequence[idx]:
                            return False
                    elif op == '<':
                        if var_sequence[target_idx] >= var_sequence[idx]:
                            return False
                if backtrack(idx + 1):
                    return True
            return False

        if backtrack(0):
            results.append(''.join(map(str, var_sequence)))
        else:
            results.append("false")

    for res in results:
        print(res)

