def execute_befunge(program):
    if not program:
        return None

    rows = []
    for line in program.splitlines():
        if '?' in line or '.' not in line:
            line = line + '.'
        rows.append(line)

    max_rows = len(rows)
    max_cols = max(len(row) if row else 0 for row in rows)

    for i in range(max_rows):
        while len(rows[i]) < max_cols:
            rows[i] += ' '

    stack = []
    pos = [0, 0]
    dir = [1, 0]
    variables = {}
    constraints = {}
    total_length = 0

    def get_char():
        r, c = pos[0], pos[1]
        if r < 0 or r >= max_rows or c < 0 or c >= len(rows[r]):
            return None
        return rows[r][c]

    def set_char(char):
        if char is not None:
            r, c = pos[0], pos[1]
            if 0 <= r < max_rows and 0 <= c < len(rows[r]):
                rows[r] = rows[r][:c] + char + rows[r][c+1:]

    def push_digit(digit):
        nonlocal stack
        stack.append(int(digit))
        d_index = len(stack) - 1
        if d_index not in constraints:
            constraints[d_index] = set()
        constraints[d_index].add('digit')

    def pop_digits(n):
        nonlocal stack, total_length, constraints
        res = []
        for _ in range(n):
            if not stack:
                return None
            res.append(stack.pop())
        total_length += n
        last_index = len(res)
        for i in range(1, n+1):
            idx = last_index - i
            if idx not in constraints:
                constraints[idx] = set()
            constraints[idx].add('digit')
        return res[::-1]

    def apply_operator(op):
        nonlocal stack
        if len(stack) < 2:
            return False
        a = stack.pop()
        b = stack.pop()
        if op == '+':
            result = b + a
        elif op == '-':
            result = b - a
        elif op == '*':
            result = b * a
        elif op == '/':
            if a == 0:
                return False
            result = b // a
        elif op == '%':
            if a == 0:
                return False
            result = b % a
        else:
            return False
        stack.append(result)
        return True

    def check_condition():
        nonlocal stack
        if not stack:
            return False
        return stack[-1] == 0

    def move(direction):
        nonlocal pos, dir
        if direction == '>':
            dir = [1, 0]
        elif direction == '<':
            dir = [-1, 0]
        elif direction == '^':
            dir = [0, -1]
        elif direction == 'v':
            dir = [0, 1]
        elif direction == '!':
            if stack and stack[-1] == 0:
                dir = [0, 1]
            else:
                dir = [0, -1]
        else:
            return False

    def change_direction(d):
        nonlocal dir
        if d == '#':
            if dir[0] > 0:
                dir = [0, 1]
            elif dir[0] < 0:
                dir = [0, -1]
            elif dir[1] > 0:
                dir = [-1, 0]
            else:
                dir = [1, 0]
        elif d == '$':
            if dir[0] != 0:
                dir = [0, dir[1]]
            else:
                dir = [dir[0], 0]
        elif d == '%':
            if dir[1] != 0:
                dir = [dir[0], 0]
            else:
                dir = [0, dir[1]]
        elif d == '&':
            if dir[0] != 0:
                if dir[0] > 0:
                    dir = [0, 1]
                else:
                    dir = [0, -1]
            else:
                if dir[1] > 0:
                    dir = [-1, 0]
                else:
                    dir = [1, 0]
        elif d == '*':
            dir = [-dir[0], -dir[1]]
        else:
            return False

    def handle_loops():
        nonlocal pos, dir
        r, c = pos[0], pos[1]
        if r < 0 or r >= max_rows or c < 0 or c >= len(rows[r]):
            return False
        ch = get_char()
        if ch is None:
            return False
        set_char('~')
        return True

    def read_char():
        nonlocal stack
        if not stack:
            return False
        stack.append(ord(input()))
        return True

    def output_char():
        nonlocal stack, rows
        if not stack:
            return False
        if len(stack) < 1:
            return False
        c = stack.pop()
        if isinstance(c, int):
            rows[pos[0]] = rows[pos[0]][:pos[1]] + str(c) + rows[pos[0]][pos[1]+1:]
        else:
            return False
        return True

    def backtrack_sequence():
        seq = []
        current_index = len(constraints) - 1
        while current_index >= 0:
            if current_index not in constraints:
                current_index -= 1
                continue
            if 'digit' in constraints[current_index]:
                seq.append(str(current_index))
            current_index -= 1
        return ''.join(seq)

    while 0 <= pos[0] < max_rows and 0 <= pos[1] < len(rows[pos[0]]):
        ch = get_char()
        if ch is None:
            break

        set_char('.')

        if ch == '0':
            push_digit(0)
        elif ch == '1':
            push_digit(1)
        elif ch == '2':
            push_digit(2)
        elif ch == '3':
            push_digit(3)
        elif ch == '4':
            push_digit(4)
        elif ch == '5':
            push_digit(5)
        elif ch == '6':
            push_digit(6)
        elif ch == '7':
            push_digit(7)
        elif ch == '8':
            push_digit(8)
        elif ch == '9':
            push_digit(9)
        elif ch == '+':
            if not apply_operator('+'):
                return None
        elif ch == '-':
            if not apply_operator('-'):
                return None
        elif ch == '*':
            if not apply_operator('*'):
                return None
        elif ch == '/':
            if not apply_operator('/'):
                return None
        elif ch == '%':
            if not apply_operator('%'):
                return None
        elif ch == '>':
            dir = [1, 0]
        elif ch == '<':
            dir = [-1, 0]
        elif ch == '^':
            dir = [0, -1]
        elif ch == 'v':
            dir = [0, 1]
        elif ch == '!':
            if stack and stack[-1] == 0:
                dir = [0, 1]
            else:
                dir = [0, -1]
        elif ch == '~':
            if read_char():
                pass
        elif ch == '@':
            r, c = pos[0], pos[1]
            stack.append(r)
            stack.append(c)
        elif ch == '#':
            change_direction('#')
        elif ch == '$':
            change_direction('$')
        elif ch == '%':
            change_direction('%')
        elif ch == '&':
            change_direction('&')
        elif ch == '*':
            change_direction('*')
        elif ch == ':':
            if len(stack) >= 2:
                stack.append(stack[-1])
        elif ch == '\\':
            if len(stack) >= 2:
                a, b = stack.pop(), stack.pop()
                stack.append(a)
                stack.append(b)
        elif ch == '_':
            if stack and stack[-1] == 0:
                dir = [-1, 0]
            else:
                dir = [1, 0]
        elif ch == '|':
            if stack and stack[-1] == 0:
                dir = [0, 1]
            else:
                dir = [0, -1]
        elif ch == '.': 
            if stack:
                c = stack.pop()
                if isinstance(c, int):
                    print(chr(c), end='')
                else:
                    return None
        elif ch == ',': 
            if not output_char():
                return None
        elif ch == 'p':
            if len(stack) < 3:
                return None
            y = stack.pop()
            x = stack.pop()
            d = stack.pop()
            rows[int(y)][int(x)] = str(d)
        elif ch == 'i':
            stack.append(ord('i'))
        elif ch == ' ': 
            pass
        else:
            return None

        r, c = pos[0], pos[1]
        dr, dc = dir[0], dir[1]
        pos[0] += dr
        pos[1] += dc

        if pos[0] < 0 or pos[0] >= max_rows or pos[1] < 0 or pos[1] >= len(rows[pos[0]]):
            break

    if not stack:
        return None

    if len(constraints) == 0:
        return None

    seq = []
    for i in range(len(constraints)):
        if i in constraints and 'digit' in constraints[i]:
            seq.append('0')

    return ''.join(seq)

