def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return

    output_lines = []
    for line in data:
        if not line.strip():
            continue
        program = line.strip()
        n = len(program)
        constraints = []
        i = 0

        while i < n:
            c = program[i]
            if c == '?':
                i += 1
                op1 = program[i]
                i += 1
                if op1 == '(':
                    j = i
                    while i < n and program[i] != ')':
                        i += 1
                    if i < n and program[i] == ')':
                        i += 1
                        continue
                else:
                    if op1 == '_':
                        constraints.append(('less',))
                        i += 1
                    elif op1 == '>':
                        constraints.append(('greater',))
                        i += 1
                    else:
                        constraints.append(('unknown',))
            elif c == '!':
                i += 1
            elif c == ':':
                i += 1
            elif c == '=':
                i += 1
            elif c == '-':
                i += 1
            elif c == '+':
                i += 1
            elif c == '>':
                i += 1
            elif c == '<':
                i += 1
            elif c == '(' or c == ')' or c == '*':
                i += 1
            elif c == '_':
                i += 1
            elif c == '.':
                i += 1
            else:
                i += 1

        num_constraints = len(constraints)
        if num_constraints == 0:
            output_lines.append("0")
            continue

        m = max(num_constraints // 2, 1)
        seq = [0] * m

        satisfied = False
        found = False
        for length in range(1, 11):
            if satisfied:
                break
            for perm in generate_permutations_of_digits(length):
                if check_constraints(seq, constraints, perm):
                    seq = perm
                    found = True
                    break
            if found:
                satisfied = True

        if satisfied:
            output_lines.append(''.join(map(str, seq)))
        else:
            output_lines.append("false")

    for res in output_lines:
        print(res)

def generate_permutations_of_digits(length):
    from itertools import product
    digits = '0123456789'
    return product(digits, repeat=length)

def check_constraints(seq, constraints, candidate):
    if len(seq) != len(candidate):
        return False

    for idx, constraint in enumerate(constraints):
        if constraint[0] == 'less':
            if idx + 1 >= len(seq):
                return False
            if candidate[idx] >= candidate[idx + 1]:
                return False
        elif constraint[0] == 'greater':
            if idx + 1 >= len(seq):
                return False
            if candidate[idx] <= candidate[idx + 1]:
                return False
        else:
            return False
    return True

