import sys

def parse_instructions(s):
    rows = []
    if len(s) == 0:
        return rows

    max_cols = len(s.split('\n')[0]) if '\n' in s else len(s)
    for i in range(0, len(s), max_cols):
        row = s[i:i+max_cols]
        while len(row) < max_cols:
            row += ' '
        rows.append(row)
    return rows

class BefungeState:
    def __init__(self, grid):
        self.grid = grid
        self.rows = len(grid)
        self.cols = len(grid[0])
        self.sp = [0]  # stack pointer, starting with one element (stack starts empty but sp points to top)
        self.stack = []
        self.pc = [0, 0]  # current position (row, col)
        self.direction = [(0, 1)]  # start moving right

    def get_current_cell(self):
        return self.grid[self.pc[0]][self.pc[1]]

    def execute_step(self):
        cell = self.get_current_cell()
        if cell == '':
            return False
        if cell.isdigit():
            num = int(cell)
            self.stack.append(num)
            self.next_pos()
            return True
        elif cell == '~':
            if self.stack:
                self.stack.pop()
            self.next_pos()
            return True
        elif cell == '>':
            self.direction = [(0, 1)]
            self.next_pos()
            return True
        elif cell == '<':
            self.direction = [(0, -1)]
            self.next_pos()
            return True
        elif cell == '^':
            self.direction = [(-1, 0)]
            self.next_pos()
            return True
        elif cell == 'v':
            self.direction = [(1, 0)]
            self.next_pos()
            return True
        elif cell == '_':
            if self.stack:
                val = self.stack.pop()
                if val == 0:
                    self.direction = [(1, 0)]
                else:
                    self.direction = [(-1, 0)]
            self.next_pos()
            return True
        elif cell == '|':
            if self.stack:
                val = self.stack.pop()
                if val == 0:
                    self.direction = [(0, -1)]
                else:
                    self.direction = [(0, 1)]
            self.next_pos()
            return True
        elif cell == '$':
            if self.stack:
                self.stack.pop()
            self.next_pos()
            return True
        elif cell == '+':
            if len(self.stack) >= 2:
                a = self.stack.pop()
                b = self.stack.pop()
                self.stack.append(a + b)
            self.next_pos()
            return True
        elif cell == '-':
            if len(self.stack) >= 2:
                a = self.stack.pop()
                b = self.stack.pop()
                self.stack.append(b - a)
            self.next_pos()
            return True
        elif cell == '*':
            if len(self.stack) >= 2:
                a = self.stack.pop()
                b = self.stack.pop()
                self.stack.append(a * b)
            self.next_pos()
            return True
        elif cell == '/':
            if len(self.stack) >= 2:
                a = self.stack.pop()
                b = self.stack.pop()
                result = b // a
                self.stack.append(result)
            self.next_pos()
            return True
        elif cell == '%':
            if len(self.stack) >= 2:
                a = self.stack.pop()
                b = self.stack.pop()
                result = b % a
                self.stack.append(result)
            self.next_pos()
            return True
        elif cell == '=':
            if len(self.stack) >= 2:
                a = self.stack.pop()
                b = self.stack.pop()
                if a == b:
                    self.stack.append(1)
                else:
                    self.stack.append(0)
            self.next_pos()
            return True
        elif cell == '!':
            if len(self.stack) >= 2:
                a = self.stack.pop()
                b = self.stack.pop()
                if a != b:
                    self.stack.append(1)
                else:
                    self.stack.append(0)
            self.next_pos()
            return True
        elif cell == '@':
            if len(self.stack) >= 1:
                count = self.stack.pop()
                if count > 0:
                    if count > len(self.stack):
                        count = len(self.stack)
                    args = [self.stack.pop() for _ in range(count)]
                    while args:
                        self.stack.append(args.pop())
            self.next_pos()
            return True
        elif cell == '#':
            if len(self.stack) >= 1:
                n = self.stack.pop()
                if n > 0:
                    self.pc[0] += 1
            self.next_pos()
            return True
        else:
            self.next_pos()
            return True

    def next_pos(self):
        dr, dc = self.direction[0]
        self.pc[0] += dr
        self.pc[1] += dc
        if self.pc[0] < 0:
            self.pc[0] = self.rows - 1
        elif self.pc[0] >= self.rows:
            self.pc[0] = 0
        if self.pc[1] < 0:
            self.pc[1] = self.cols - 1
        elif self.pc[1] >= self.cols:
            self.pc[1] = 0

    def is_running(self):
        r, c = self.pc[0], self.pc[1]
        if r < 0 or r >= self.rows or c < 0 or c >= self.cols:
            return False
        cell = self.grid[r][c]
        if cell in ['>', '<', '^', 'v', '_', '|']:
            return True
        return cell != ''

    def feed(self):
        digits = []
        while self.is_running():
            cell = self.get_current_cell()
            if cell.isdigit():
                for d in range(0, 10):
                    saved_state = (
                        (self.pc[0], self.pc[1]), 
                        tuple(self.stack[:]),
                        tuple(self.direction),
                        digits[:]
                    )
                    self.stack.append(d)
                    if not self.execute_step():
                        digits.append(d)
                        self.stack.pop()
                        return digits
                    else:
                        self.pc[0], self.pc[1] = saved_state[0]
                        self.stack = list(saved_state[1])
                        self.direction = list(saved_state[2])
                        digits = list(saved_state[3])
                        self.stack.pop()
                return None
            elif cell == '~':
                if not self.execute_step():
                    return None
            else:
                if not self.execute_step():
                    return None
        return digits

