import sys

def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return

    output_lines = []
    for line in data:
        s = line.strip()
        if not s:
            continue

        parts = s.split(':', 1)
        if len(parts) < 2:
            output_lines.append("false")
            continue

        pattern_part = parts[0].strip()
        condition_part = parts[1].strip()

        if pattern_part.startswith('?'):
            pattern_part = pattern_part[1:]
        else:
            output_lines.append("false")
            continue

        if pattern_part.startswith('('):
            pattern_part = pattern_part[1:]
        else:
            output_lines.append("false")
            continue

        if not pattern_part.endswith(')'):
            output_lines.append("false")
            continue
        pattern_part = pattern_part[:-1]

        condition_parts = condition_part.split(',')
        total_underscores = 0
        for part in condition_parts:
            part = part.strip()
            if not part:
                continue
            if '_' in part:
                count = part.count('_')
                total_underscores += count

        n = total_underscores + 1

        conditions = []
        for part in condition_parts:
            part = part.strip()
            if '_' not in part:
                continue
            tokens = part.split()
            for token in tokens:
                if '>' in token:
                    left_count = token.count('_') while '_' in token.split('>')[0] else 0
                    right_count = token.count('_') while '_' in token.split('>')[1] else 0
                    conditions.append((left_count, '>', right_count))
                elif '<' in token:
                    left_count = token.count('_') while '_' in token.split('<')[0] else 0
                    right_count = token.count('_') while '_' in token.split('<')[1] else 0
                    conditions.append((left_count, '<', right_count))

        if not conditions:
            seq = '0' * n
            output_lines.append(seq)
            continue

        def is_feasible(seq):
            valid = True
            for cond in conditions:
                left_len, op, right_len = cond
                start_left = len(seq) - left_len - right_len
                if start_left < 0:
                    valid = False
                    break
                left_segment = seq[start_left:start_left+left_len]
                right_segment = seq[start_left+left_len:start_left+left_len+right_len]
                if op == '>':
                    if int(left_segment) > int(right_segment):
                        continue
                    else:
                        valid = False
                        break
                elif op == '<':
                    if int(left_segment) < int(right_segment):
                        continue
                    else:
                        valid = False
                        break
            return valid

        seq = ['0'] * n
        found = False
        for i in range(n):
            for d in range(9 - int(seq[i]), 10):
                new_seq = seq[:i] + [str(d)] + seq[i+1:]
                if is_feasible(''.join(new_seq)):
                    seq = new_seq
                    found = True
                    break
            if found:
                break

        if found:
            output_lines.append(''.join(seq))
        else:
            output_lines.append("false")

    for out in output_lines:
        print(out)

if __name__ == "__main__":
    main()

import sys
from collections import deque

def factorize(n):
    factors = []
    i = 1
    while i * i <= n:
        if n % i == 0:
            factors.append((i, n // i))
        i += 1
    return factors

class BefungeInterpreter:
    def __init__(self, grid, R, C):
        self.grid = grid
        self.R = R
        self.C = C
        self.stack = deque()
        self.pc = [0, 0]
        self.direction = [0, 1]  # [dr, dc]: initially moving right
        self.vars = {}
        self.constraints = []
        self.completed = False
        self.error = False

    def get(self, r, c):
        if 0 <= r < self.R and 0 <= c < self.C:
            return self.grid[r][c]
        return None

    def set(self, r, c, ch):
        if 0 <= r < self.R and 0 <= c < self.C:
            self.grid[r][c] = ch

    def move(self):
        r, c = self.pc
        dr, dc = self.direction
        new_r = r + dr
        new_c = c + dc
        if new_r < 0:
            new_r = self.R - 1
        elif new_r >= self.R:
            new_r = 0
        if new_c < 0:
            new_c = self.C - 1
        elif new_c >= self.C:
            new_c = 0
        self.pc = [new_r, new_c]

    def execute(self, s):
        self.grid = s
        self.R = len(s)
        self.C = len(s[0]) if self.R > 0 else 0
        self.stack = deque()
        self.pc = [0, 0]
        self.direction = [0, 1]
        self.vars = {}
        self.constraints = []
        self.completed = False
        self.error = False
        while not self.completed and not self.error:
            r, c = self.pc
            if r < 0 or r >= self.R or c < 0 or c >= self.C:
                self.error = True
                break
            ch = self.grid[r][c]
            if ch is None:
                self.error = True
                break
            if ch in '0123456789':
                num = int(ch)
                self.stack.append(num)
            elif ch == '+':
                if len(self.stack) < 2:
                    self.error = True
                    break
                a = self.stack.pop()
                b = self.stack.pop()
                self.stack.append(a + b)
            elif ch == '-':
                if len(self.stack) < 2:
                    self.error = True
                    break
                a = self.stack.pop()
                b = self.stack.pop()
                self.stack.append(b - a)
            elif ch == '*':
                if len(self.stack) < 2:
                    self.error = True
                    break
                a = self.stack.pop()
                b = self.stack.pop()
                self.stack.append(a * b)
            elif ch == '/':
                if len(self.stack) < 2:
                    self.error = True
                    break
                a = self.stack.pop()
                b = self.stack.pop()
                if a == 0:
                    self.stack.append(0)
                else:
                    self.stack.append(b // a)
            elif ch == '%':
                if len(self.stack) < 2:
                    self.error = True
                    break
                a = self.stack.pop()
                b = self.stack.pop()
                if a == 0:
                    self.stack.append(0)
                else:
                    self.stack.append(b % a)
            elif ch == '>':
                self.direction = [0, 1]
            elif ch == '<':
                self.direction = [0, -1]
            elif ch == '^':
                self.direction = [-1, 0]
            elif ch == 'v':
                self.direction = [1, 0]
            elif ch == '_':
                if self.stack and self.stack[-1] == 0:
                    self.direction = [1, 0]
                else:
                    self.direction = [0, -1]
            elif ch == '|':
                if self.stack and self.stack[-1] == 0:
                    self.direction = [-1, 0]
                else:
                    self.direction = [0, 1]
            elif ch == '$':
                if len(self.stack) < 1:
                    self.error = True
                    break
                self.stack.pop()
            elif ch == '.': 
                if len(self.stack) < 1:
                    self.error = True
                    break
                self.completed = True
                self.vars['output'] = self.stack.pop()
            elif ch == ',': 
                if len(self.stack) < 1:
                    self.error = True
                    break
                self.vars['output'] = self.stack.pop()
            elif ch == ':':
                if len(self.stack) < 1:
                    self.error = True
                    break
                self.stack.append(self.stack[-1])
            elif ch == '?':
                if len(self.stack) < 1:
                    self.error = True
                    break
                if self.stack[-1] == 0:
                    self.direction = [0, 1]
                else:
                    self.direction = [0, -1]
            elif ch == '@':
                if len(self.stack) < 2:
                    self.error = True
                    break
                a = self.stack.pop()
                b = self.stack.pop()
                self.swap(a, b)
            elif ch == '!':
                if len(self.stack) < 1:
                    self.error = True
                    break
                self.stack.pop()
            elif ch == '#':
                if len(self.stack) < 2:
                    self.error = True
                    break
                a = self.stack.pop()
                b = self.stack.pop()
                self.stack.append(a)
                self.stack.append(b)
            elif ch == '\\':
                if len(self.stack) < 2:
                    self.error = True
                    break
                a = self.stack.pop()
                b = self.stack.pop()
                self.stack.append(a)
                self.stack.append(b)
            elif ch == '0' and len(self.stack) < 1:
                self.stack.append(0)
            else:
                self.error = True
            if not self.error:
                r, c = self.pc
                dr, dc = self.direction
                new_r = r + dr
                new_c = c + dc
                if new_r < 0:
                    new_r = self.R - 1
                elif new_r >= self.R:
                    new_r = 0
                if new_c < 0:
                    new_c = self.C - 1
                elif new_c >= self.C:
                    new_c = 0
                self.pc = [new_r, new_c]
        return self.vars, self.error

def solve_befunge(program_str):
    n = len(program_str)
    factors = factorize(n)
    candidates = []
    for r, c in factors:
        if r <= n and c <= n and r * c == n:
            candidates.append((r, c))
    if not candidates:
        return "false"
    grid = []
    for i in range(len(candidates)):
        r_dim, c_dim = candidates[i]
        grid_rows = []
        idx = 0
        for j in range(r_dim):
            row = []
            for k in range(c_dim):
                if idx < n:
                    row.append(program_str[idx])
                    idx += 1
                else:
                    row.append(' ')
            grid_rows.append(''.join(row))
        grid.append(grid_rows)
    results = []
    for grid_rows in grid:
        interpreter = BefungeInterpreter(grid_rows, len(grid_rows), len(grid_rows[0]))
        vars_dict, error = interpreter.execute(grid_rows)
        if error:
            results.append("false")
        else:
            output_val = vars_dict.get('output', None)
            if output_val is not None and isinstance(output_val, int):
                results.append(str(output_val))
            else:
                results.append("false")
    return results

