import sys
import re

def parse_golorp(name):
    # Extract the pattern from the name
    pattern = re.search(r'^(?:\?|\.)(.*?)(?=\:)', name).group(1)

    # Parse the constraints
    constraints = []
    parts = name.split(':')
    for part in parts[1:]:
        if not part:
            continue
        if ',' in part:
            left, right = part.split(',')
            left_var, left_val = left.split('>')
            right_var, right_val = right.split('<')
            constraints.append((left_var, left_val, right_var, right_val))
        else:
            var, val = part.split('>')
            constraints.append((var, val, None, None))

    return pattern, constraints

def is_valid_sequence(pattern, sequence):
    if len(sequence) != len(pattern):
        return False
    for i, ch in enumerate(pattern):
        if ch == '?':
            continue
        elif ch == '_':
            if sequence[i] == '0':
                return False
        elif ch == '-':
            if sequence[i] == '9':
                return False
        elif ch == '*':
            if sequence[i] == '0':
                return False
        elif ch == '+':
            if sequence[i] == '9':
                return False
        elif ch == '/':
            if sequence[i] == '0':
                return False
        elif ch == '(' or ch == ')':
            continue
        elif ch == '<' or ch == '>':
            continue
        else:
            return False
    return True

def find_lex_smallest(pattern, constraints):
    from itertools import product

    # Determine the number of variables needed
    num_vars = sum(1 for ch in pattern if ch in ['?', '_', '-', '*', '+', '/'])

    # Generate all possible sequences of length num_vars with digits 0-9
    for seq in product('0123456789', repeat=num_vars):
        seq_str = ''.join(seq)
        if not is_valid_sequence(pattern, seq_str):
            continue

        # Check all constraints
        valid = True
        for var1, val1, var2, val2 in constraints:
            idx1 = pattern.find(var1)
            idx2 = pattern.find(var2)
            if idx1 == -1 or idx2 == -1:
                continue
            if seq_str[idx1] >= val1 and (var2 is None or seq_str[idx2] <= val2):
                continue
            valid = False
            break
        if valid:
            return seq_str
    return "false"

def main():
    input_lines = sys.stdin.read().strip().split('\n')
    for line in input_lines:
        if not line:
            continue
        pattern, constraints = parse_golorp(line)
        result = find_lex_smallest(pattern, constraints)
        print(result)

if __name__ == "__main__":
    main()

