import re

def parse_golorp(name):
    # Extract the pattern from the name
    pattern = re.search(r'^(?:\?|\.)(.*?)(?=\:)', name).group(1)

    # Parse the constraints
    constraints = []
    parts = name.split(':')
    for part in parts[1:]:
        if not part:
            continue
        if ',' in part:
            # Handle multiple constraints
            for constraint in part.split(','):
                if '<' in constraint:
                    left, right = constraint.split('<')
                    constraints.append((left.strip(), right.strip()))
                elif '>' in constraint:
                    left, right = constraint.split('>')
                    constraints.append((right.strip(), left.strip()))
        else:
            if '<' in part:
                left, right = part.split('<')
                constraints.append((left.strip(), right.strip()))
            elif '>' in part:
                left, right = part.split('>')
                constraints.append((right.strip(), left.strip()))

