    n = int(data[0])
    s = data[1].strip()

    total_teams = 1 << n

    def solve_case(x):
        current_phase = 0
        bracket = [list(range(total_teams)) for _ in range(n)]

        for round_idx in range(n):
            num_games = 1 << (n - round_idx - 1)
            for game_idx in range(num_games):
                left_team = bracket[round_idx][game_idx * 2]
                right_team = bracket[round_idx][game_idx * 2 + 1]
                if s[current_phase] == '1':
                    if x == left_team:
                        winner = left_team
                    elif x == right_team:
                        winner = right_team
                    else:
                        return False
                    bracket[round_idx+1][game_idx] = winner if winner > x else x
                else:
                    if x == left_team:
                        winner = left_team
                    elif x == right_team:
                        winner = right_team
                    else:
                        return False
                    bracket[round_idx+1][game_idx] = winner if winner < x else x
            current_phase += 1

        return True

    winning_integers = []
    for x in range(1, total_teams + 1):
        if solve_case(x):
            winning_integers.append(str(x))

    print(" ".join(winning_integers))

