                # Lower skill wins: so x must be in the bottom half of the current interval
                if low_bound > x:
                    valid = False
                    break
                else:
                    new_high = low_bound - 1
                    if new_high < 1:
                        break
                    high_bound = new_high

        if valid:
            res.append(str(x))

