    def is_same_side(point, road):
        a, b, c = road
        val = a * point[0] + b * point[1] + c
        return val > 0

    # Function to determine if two points are in the same block
    def same_block(p1, p2):
        for a, b, c in roads:
            val1 = a * p1[0] + b * p1[1] + c
            val2 = a * p2[0] + b * p2[1] + c
            if val1 * val2 < 0:
                return False
        return True

    # BFS to find minimum steps
    visited = set()
    queue = deque()
    queue.append((home, 0))
    visited.add(home)

    while queue:
