import sys
import math
from collections import deque

def readints():
    return list(map(int, sys.stdin.readline().split()))

def cross(a, b):
    return a[0] * b[1] - a[1] * b[0]

def sign(x):
    return 1 if x > 0 else -1 if x < 0 else 0

def is_point_on_line(x, y, a, b, c):
    return abs(a * x + b * y + c) < 1e-8

def get_intersection(line1, line2):
    a1, b1, c1 = line1
    a2, b2, c2 = line2
    det = a1 * b2 - a2 * b1
    if abs(det) < 1e-8:
        return None
    x = (c1 * b2 - c2 * b1) / det
    y = (a1 * c2 - a2 * c1) / det
    return (x, y)

def main():
    x1, y1 = readints()
    x2, y2 = readints()
    n = int(sys.stdin.readline())
    roads = [tuple(map(int, sys.stdin.readline().split())) for _ in range(n)]

    # Check if home and university are on the same block
    if is_same_block(x1, y1, x2, y2, roads):
        print(0)
        return

    # Build graph of blocks connected by roads
    visited = set()
    queue = deque()
    queue.append((x1, y1))
    visited.add((x1, y1))
    steps = 0

    while queue:
        for _ in range(len(queue)):
            x, y = queue.popleft()
            for i in range(n):
                for j in range(i + 1, n):
                    line_i = roads[i]
                    line_j = roads[j]
                    inter = get_intersection(line_i, line_j)
                    if not inter:
                        continue
                    # Check if the point (x, y) is on the same side of both lines
                    d1 = line_i[0] * x + line_i[1] * y + line_i[2]
                    d2 = line_j[0] * x + line_j[1] * y + line_j[2]
                    if d1 * d2 < 0:
                        # The point is on opposite sides of the two lines, so it's on the edge
                        # Try to move to the other side
                        new_x, new_y = inter
                        if not is_point_on_line(new_x, new_y, line_i[0], line_i[1], line_i[2]):
                            new_x, new_y = inter
                            if not is_point_on_line(new_x, new_y, line_j[0], line_j[1], line_j[2]):
                                new_x, new_y = inter
                        if (new_x, new_y) not in visited:
                            visited.add((new_x, new_y))
                            queue.append((new_x, new_y))
        if (x2, y2) in visited:
            print(steps)
            return
        steps += 1

    print(-1)

def is_same_block(x1, y1, x2, y2, roads):
    for i in range(len(roads)):
        a, b, c = roads[i]
        d1 = a * x1 + b * y1 + c
        d2 = a * x2 + b * y2 + c
        if sign(d1) != sign(d2):
            return False
    return True

