    a_red = a_val // g
    b_red = b_val // g

    def solve_quadratic(n):
        n_mod = n % a_red
        inv = pow(b_red, -1, a_red) if a_red > 1 else 1
        s0 = (-n_mod) * inv % a_red
        if s0 < 0:
            s0 += a_red
        return s0

    def f_x(x):
        if x == 0:
            return 1
        x_red = x // g
        base = (x_red + 1) * g
        count = x_red + 1
        if a_red == 0 and b_red == 0:
            return 1
        if a_red == 0:
            if x_red >= 0:
                count = 1
            else:
                count = 0
        elif b_red == 0:
            if x_red >= 0:
                count = x_red + 1
            else:
                count = 0
        else:
            for r in range(-a_red + 1, a_red):
                k0 = r
                s0 = solve_quadratic(k0)
                if s0 * b_red <= k0 * g:
                    continue
                k_min = (k0 + s0 * b_red) // a_red
                if k_min < 0:
                    k_min = 0
                if k_min <= x_red:
                    num_terms = x_red - k_min + 1
                    if num_terms > 0:
                        count += num_terms
        return count

    total_sum = 0
