                    subtree_nodes[p].extend(subtree_nodes[node])
                    subtree_count[p] += subtree_count[node]
                    subtree_nodes_count[p] = len(subtree_nodes[p])
                    subtree_edges.update(subtree_edges)
            else:
                pass

    kept_nodes = set()
    visited = [False] * (n+1)

    for node in order:
        if node in kept_nodes:
            continue
        if visited[node]:
            continue

        size = 0
        stack = [node]
        visited[node] = True
        component_nodes = set()
        component_nodes.add(node)
        size += 1

        while stack:
            cur = stack.pop()
            for neighbor in graph[cur]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    component_nodes.add(neighbor)
                    size += 1
                    stack.append(neighbor)

        if size <= n - k:
            kept_nodes.update(component_nodes)
        else:
            for x in component_nodes:
                if x in kept_nodes:
                    kept_nodes.remove(x)

    result = sorted([node for node in range(1, n+1) if node not in kept_nodes])
    print(" ".join(map(str, result)))

