        print(1)
        return

    kept_set = set(range(n-m+1, n+1))
    removed_set = set(range(1, n-m+1))

    def bfs(start, graph, visited, target_set):
        queue = deque([start])
        visited.add(start)
        component = set()
        while queue:
            node = queue.popleft()
            component.add(node)
            for neighbor in graph[node]:
                if neighbor in target_set and neighbor not in visited:
                    visited.add(neighbor)
                    queue.append(neighbor)
        return component

    visited_all = set()
    components = []
    for node in sorted(kept_set):
        if node not in visited_all:
            comp = bfs(node, graph, visited_all, kept_set)
            components.append(comp)

