    k_val = int(data[1])
    f_val = int(data[2])

    total_nodes = 2 * (2**n - 1)
    if n == 0:
        total_nodes = 0

    def nCr(n, r):
        if r < 0 or r > n:
            return 0
        num = 1
        den = 1
        for i in range(1, r + 1):
            num = num * (n - i + 1) % MOD
            den = den * i % MOD
        return num * pow(den, MOD - 2, MOD) % MOD

    def count_configs(max_size):
        if max_size <= 0:
            return pow(2, total_nodes, MOD)

        dp = [0] * (max_size + 1)
        dp[0] = 1
        for i in range(1, n + 1):
            prev_dp = dp[:]
            dp = [0] * (max_size + 1)
            for t in range(max_size + 1):
                ways_left = 0
                for prev_t in range(t + 1):
                    if prev_dp[prev_t] == 0:
                        continue
                    if t - prev_t <= max_size:
                        ways_left = (ways_left + nCr(total_nodes // (2**(i-1)), prev_t)) % MOD
                dp[t] = ways_left
            if all(x == 0 for x in dp):
                break
        total_ways = 0
        for t in range(max_size + 1):
            total_ways = (total_ways + dp[t]) % MOD
        return total_ways

    total_f = count_configs(f_val)
    total_f_minus = count_configs(f_val - 1)
    result = (total_f - total_f_minus) % MOD
    print(result)

