        if r < 0 or r > n:
            return 0
        num = 1
        den = 1
        for i in range(1, r + 1):
            num = num * (n - i + 1) % mod
            den = den * i % mod
        inv_den = pow(den, mod - 2, mod)
        return num * inv_den % mod

    def nCr_mod(n, r, mod=MOD):
        if r < 0 or r > n:
            return 0
        num = 1
        den = 1
        for i in range(1, r + 1):
            num = num * (n - i + 1) % mod
            den = den * i % mod
        inv_den = pow(den, mod - 2, mod)
        return num * inv_den % mod

    def C(n, r):
        if r < 0 or r > n:
            return 0
        res = 1
        for i in range(r):
            res = res * (n - i) // (i + 1)
        return res

    def count_ways_for_node(depth, total_strings_at_depth):
        if depth == n:
            return 1
        ways = 0
        for x in range(0, f + 1):
