            if y < 0 or y > k * (1 << (n - depth - 1)):
                continue
            if x == 0:
                ways += comb(k, y) * (C((1 << (n - depth - 1)) - 1, y)) 
            else:
                ways += comb(k, x) * comb(k, y) * (C((1 << (n - depth - 1)) - 1, x) * C((1 << (n - depth - 1)) - 1, y))
        return ways

